CREATE TABLE "workouts" ("id" uuid NOT NULL, "created_at" timestamptz NOT NULL, "updated_at" timestamptz NOT NULL, "healthkit_workout_activity_type" character varying NOT NULL, "healthkit_workout_id" character varying NULL, "average_heart_rate" character varying NULL, "distance" character varying NULL, "duration" character varying NULL, "energy_burned" character varying NULL, "workout_profile" uuid NOT NULL, "workout_competition" uuid NOT NULL, PRIMARY KEY ("id"), CONSTRAINT "workouts_profiles_profile" FOREIGN KEY ("workout_profile") REFERENCES "profiles" ("id") ON DELETE NO ACTION, CONSTRAINT "workouts_competitions_competition" FOREIGN KEY ("workout_competition") REFERENCES "competitions" ("id") ON DELETE NO ACTION);
